import {Post} from '../index';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
const Profile_recents = ({id}) => {
  
  
  useEffect(() => {
    axios.get("http://localhost:4000/api/posts")
      .then(res => {
        const regularPosts = res.data.filter(post => post.type === 'regular').sort((a, b) => new Date(b.entryDate) - new Date(a.entryDate));
        setPosts(regularPosts);
        setTotalPages(Math.ceil(regularPosts.length / PostsPerPage));
      }).catch(err => console.log(err));
  }, []);


  return (
    <div className='profile_recents__container'>
      <p className='profile_recents__container-text'>Recent Posts:</p>
      <div className='profile_recents__container-posts'>
        {/* <Post></Post> */}
      </div>
    </div>
  )
}

export default Profile_recents
