import React, { useState, useCallback } from 'react';
import './body.css';

const Body = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);
  const [fileName, setFileName] = useState("Upload Profile Pic");

  const handleDragOver = useCallback((event) => {
    event.stopPropagation();
    event.preventDefault();
    setDragOver(true);
  }, []);

  const handleDragLeave = useCallback(() => {
    setDragOver(false);
  }, []);

  const handleDrop = useCallback((event) => {
    event.stopPropagation();
    event.preventDefault();
    setDragOver(false);

    const files = event.dataTransfer.files;
    validateAndUpload(files[0]);
  }, []);

  const handleChange = useCallback((event) => {
    if (event.target.files.length > 0) {
      validateAndUpload(event.target.files[0]);
    }
  }, []);

  const validateAndUpload = (file) => {
    if (file && (file.type === "image/png" || file.type === "image/jpeg")) {
      setSelectedFile(file);
      setFileName("Image uploaded: " + file.name);
      
      // For image preview purposes, convert file to base64 string
      const reader = new FileReader();
      reader.onload = function(e) {
        // Set the preview image or perform additional actions here
        console.log(e.target.result); // This logs the base64 string for the image
      };
      reader.readAsDataURL(file);
    } else {
      alert("Please select a valid image file (PNG or JPG)");
    }
  };

  return (
    <div className="container">
      <div className="content">
        {/* Other form fields */}

        <div className={`input-box profile-pic ${dragOver ? 'drag-over' : ''}`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}>
          <label htmlFor="profilePicInput" className="upload-area">
            <p>{fileName}</p>
            <input 
              type="file" 
              id="profilePicInput" 
              accept=".png, .jpg, .jpeg" 
              hidden 
              onChange={handleChange} 
            />
          </label>
        </div>

        {/* Other form fields */}
      </div>
    </div>
  );
}

export default Body;
