import React, { useEffect, useState } from 'react';
import 'draft-js/dist/Draft.css';
import './create_course.css';
import axios from 'axios';

const Create_course = ({user}) => {
    const [courses, setCourses] = useState([]);
    // const [user, setUser] = useState(null);

    // useEffect(() => {
    //     axios.get('http://localhost:4000/api/current_user')
    //       .then(response => {
    //         const { username, _id } = response.data;
    //         setUser({ username, _id }); 
    //       })
    //       .catch(error => {
    //         console.error('Error fetching user:', error);
    //       });
    // }, []);
    const resetForm = () => {
        document.getElementById('title').value = '';
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission
        const title = document.getElementById('title').value;
        const course = document.getElementById('course').value;
        const content = document.getElementById('content').value;
        const topics = document.getElementById('topics').value;
        //const type = document.getElementById('type').value;
        const topicsArray = topics.split(' ').map(topic => topic.replace(/^#/, ''));
        let type = 'regular';        
        const formData = {
            title,
            course,
            content,
            topicsArray,
            username: String(user.username),
            type
        };
        
        axios.course("http://localhost:4000/api/courses", formData)
            .then(response => {
                // Handle success
                console.log(response.data);
                alert("Post created successfully!");
                window.location.href = "/";
            })
            .catch(error => {
                // Handle error
                console.log(error);
            });
            
        console.log(formData);
        resetForm();
    };





    return (

        <div className="create-course-container">
                <div className='form-group-row'>
        
                    <div className="form-group-title">
                        <p className='create-course-header'>Course Name</p>
                        <input id='title' type="text" className="title-input" placeholder="Input Course Name Here" />
                    </div>
                </div>
                <button onClick={handleSubmit} className="course-button">Create Course</button>
        </div>
    );
};

export default Create_course;
