import React from 'react'
import './sidebardropdown.css';
import dropdown from '../../assets/icons/dropdown.svg';


function clickMenu(id) {
  console.log(id);
  document.getElementById(id).classList.toggle("show");
}

const Sidebardropdown = ({title,contents}) => {
  const listItems = contents.map(content =>
    <li>{content}</li>
  );
  return (
    <div className='sidebardropdown'>
      <button onClick={() => clickMenu({title})} className='sidebardropdown__button'>
        <p>{title}</p>
        <img src={dropdown} alt='dropdown'/>
      </button>
      <ul id={title} className='sidebardropdown__list'>
        {listItems}
      </ul>

    </div>
  )
}

export default Sidebardropdown
