import React, { useEffect, useState } from 'react'
import './course_container.css';
//import {Post} from '../../components/index';
import axios from 'axios';
import {Link, useParams} from 'react-router-dom'


const Course_container = () => {
  const {id} = useParams();
  const [course,setCourse] = useState([])

  useEffect( () => {
    axios.get("http://localhost:4000/api/course")
      .then(res => setCourse(res.data)
      ).catch(err=> console.log(err));
  },[]);

  // const Course = posts.slice(0,2).map((content, index) =>
  //   <Post contents={content}></Post>
  // );
  
  const Course = course.map((content, index) => 
    <Link to={'/course/'+content.name}>{'cc/'+content.name}</Link>
  );

  return (
    <div className='course__container section__padding'>
      <h1>Course </h1>
      <div className='rectangleline'></div>
      <p>{id}</p>
    </div>
  )
}

export default Course_container
