import React from 'react'
import './create_post.css';

const Create_post = ({imgUrl, name, action}) => {
  return (
    <div className="create-post-container">
    <input type="text" className="title-input"/>
    <select className="course-select">
      <option className='text-format'>-Course-</option>=
      <option className='text-format'>CCAPDEV</option>=
      <option className='text-format'>CCPROG1</option>=
      </select>
      <textarea className="text-input" placeholder="input text here"></textarea>
      <button className="post-button">Post</button>
    </div>
  )
}

export default Create_post;
