import React, { useState } from 'react';
import { Editor, EditorState, RichUtils } from 'draft-js';
import 'draft-js/dist/Draft.css';
import './create_post.css';

const Create_post = ({ imgUrl, name, action }) => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  const handleKeyCommand = (command) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState);
      return 'handled';
    }
    return 'not-handled';
  };

  const onBoldClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'BOLD'));
  };

  const onItalicClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'ITALIC'));
  };

  // Add other formatting functions as needed

  return (
    <div className="create-post-container">
      {/* Other form groups remain the same */}
      <div className='form-group-column'>
        <h1 className='create-post-topic'>Choose topics (separated by comma)</h1>
        <div className="topic-input-container">
          <input type="text" className="topic-input" placeholder="input topics here" />
        </div>
        <div className="editor-toolbar">
          <button onMouseDown={(e) => e.preventDefault()} onClick={onBoldClick}>Bold</button>
          <button onMouseDown={(e) => e.preventDefault()} onClick={onItalicClick}>Italic</button>
          {/* Add more buttons for additional styles */}
        </div>
        <div className="editor-container">
          <Editor
            editorState={editorState}
            handleKeyCommand={handleKeyCommand}
            onChange={setEditorState}
          />
        </div>
        <button className="post-button">Post</button>
      </div>
    </div>
  );
};

export default Create_post;
