import React, { useEffect, useState } from 'react';
import './post_full_container.css';
import { Post_full } from '../../components/index';
import axios from 'axios';
import {Link, useParams} from 'react-router-dom'

const Posts_container = () => {
  const {id} = useParams();
  const [posts, setPosts] = useState([]);
  
  useEffect(() => {
    axios.get("http://localhost:4000/api/posts")
      .then(res => {
        const FullPost = res.data.filter(
          post => post.type === 'regular' && post._id === id);
        setPosts(FullPost);
      }).catch(err => console.log(err));
  }, []);

  return (
    <div className='post_full_container__container section__padding'>
      <Post_full ></Post_full>
    </div>
  );
}

export default Posts_container;
