import React, { useState } from 'react';
import './style.css'; // Make sure the path to your CSS file is correct
// You can also import the Font Awesome CSS in your index.js or App.js file, or here directly if you prefer

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordShown, setPasswordShown] = useState(false);

  const togglePasswordVisibility = () => {
    setPasswordShown(!passwordShown);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Implement your login logic here
    console.log('Logging in with:', { username, password });
    // Redirect or do something upon login
  };

  return (
    <div className="container">
      <form id="form" className="form" onSubmit={handleLogin}>
        <h2>Welcome to <br />
          <span id="title">Taft Overflow</span>
        </h2>
        <div className="form-control">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-control">
          <label htmlFor="password">Password</label>
          <input
            type={passwordShown ? "text" : "password"}
            id="password"
            name="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <i
            className="fa-solid fa-eye toggle-password"
            onClick={togglePasswordVisibility}
          ></i>
        </div>
        <div id="signup">Not a member? <a href="Register.html" id="register">Sign up</a> now!</div>
        <button type="submit" id="login-button">Login</button>
      </form>
      
      {/* Assuming you want to redirect on click, React Router or a similar approach should be used instead of an anchor tag for navigation. */}
    </div>
  );
};

export default LoginForm;
