import React from 'react'
import './post_full.css';
import {profile,dropdown, upvote_highlight,downvote_highlight,downvote_none,upvote_none} from './imports';
import study from '../../assets/images/study.jpg';
import {Link} from 'react-router-dom';

function formatDate(date) {
  return new Date(date).toLocaleDateString("en-US", {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
}


const Post_full = ({contents}) => {
  
  const post_full_id = contents._id;
  const title = contents.title;
  const content = contents.content;
  const date = formatDate(contents.entryDate);
  const course_id = contents.course_id.name;
  const username = contents.user_id.name;
  const profile_image = contents.user_id.profile_img;
  const type = contents.type;
  const topic_ids = contents.topic_ids;

  const query_tags = topic_ids.map((content, index) => 
    <a href={'/topics/'+content.name}>{'#'+content.name}</a>
  );



  return (
    <div className='post_full__container'>
      <div className='post_full__header'>
        <div className='post_full__footer'>
          <div className='post_full__footer-tags'>
            {query_tags}
          </div>
          <Link className='post_full__footer-view' to={'/post_fulls/'+ post_full_id}>
            <img src={dropdown}></img>
            <p>View more</p>
          </Link>
          <div className='post_full__footer-date'>
            <p>{date}</p>
          </div>
        </div>

        <div className='post_full__profile'>
          <div className='post_full__profile-image'>
            <img src={profile} alt='profile'/>
          </div>
          <div className='post_full__profile-name'>
            <p>{username}</p>
          </div>
        </div>
        <div className='post_full__location'>
          <p>Post_fulled in </p>
          <a href='#'>c/{course_id}</a>
        </div>
        <div className='post_full__votes'>
          <button class='post_full__votes-upvote'><img src={upvote_none} alt='upvote'></img></button>
          <p>123</p>
          <button class='post_full__votes-downvote'><img src={downvote_none} alt='downvote'></img></button>
          <p>123</p>
        </div>
      </div>
      <div className='post_full__content'>
        <p>{content}</p>
      </div>
    </div>

    
  )
}

export default Post_full
