import React, { useState } from 'react';

// Recursive comment component to handle infinite nesting
const Comment = ({ comment, onReply }) => {
    const [showReply, setShowReply] = useState(false);
    const [replyText, setReplyText] = useState('');
  
    const handleReply = () => {
      onReply(replyText, comment.id);
      setReplyText('');
      setShowReply(false);
    };
  
    return (
      <div className="comment">
        <div className="comment-content">
          <strong>{comment.author}:</strong> {comment.text}
        </div>
        {comment.replies?.length > 0 && (
          <div className="comment-replies">
            {comment.replies.map(reply => (
              <Comment key={reply.id} comment={reply} onReply={onReply} />
            ))}
          </div>
        )}
        <button onClick={() => setShowReply(!showReply)}>Reply</button>
        {showReply && (
          <div className="comment-reply">
            <textarea value={replyText} onChange={(e) => setReplyText(e.target.value)} placeholder="Write a reply..." />
            <button onClick={handleReply}>Submit Reply</button>
          </div>
        )}
      </div>
    );
  };
  
// Helper function to recursively add a reply to the appropriate comment
const addReply = (comments, parentId, newComment) => {
  return comments.map(comment => {
    if (comment.id === parentId) {
      return { ...comment, replies: [...comment.replies, newComment] };
    } else if (comment.replies) {
      return { ...comment, replies: addReply(comment.replies, parentId, newComment) };
    } else {
      return comment;
    }
  });
};

// Main comment section component
const CommentsSection = () => {
  const [comments, setComments] = useState([]);
  const [newCommentText, setNewCommentText] = useState('');

  const postComment = (text, parentId = null) => {
    if (!text.trim()) {
      alert('Please enter a comment or reply.');
      return;
    }
    const comment = {
      id: Math.random().toString(36).substr(2, 9),
      author: 'Anonymous',
      text,
      replies: [],
    };

    if (parentId) {
      setComments(prevComments => addReply(prevComments, parentId, comment));
    } else {
      setComments(prevComments => [...prevComments, comment]);
    }

    setNewCommentText('');
  };

return (
    <div className="comments-section">
        <textarea
            className="commentInput"
            value={newCommentText}
            onChange={(e) => setNewCommentText(e.target.value)}
            placeholder="Write a comment..."
        />
        <button id="submit-button" onClick={() => postComment(newCommentText)}>Post Comment</button>
        <div className="comments-list">
            {comments.map(comment => (
                <Comment key={comment.id} comment={comment} onReply={postComment} />
            ))}
        </div>
    </div>
);
};

export default CommentsSection;
