  const [confirmPassword, setConfirmPassword] = useState('');
  const [idNumber, setIdNumber] = useState('');
  const [degree, setDegree] = useState('');
  const [aboutYou, setAboutYou] = useState('');
  const [email, setEmail] = useState('');
