import React, { useState } from 'react';
import './body.css';

const Body = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Form submitted:", { username, password, rememberMe });
  };

  return (
<div class="container">
  <div class="container__welcome">Welcome to</div>
  <div class="container__title">Taft Overflow</div>
  <div class="content">
    <form class="form">
      <div class="form__user-details">
        <div class="form__user-details-item">
          <div class="input-box">
            <input 
              class="input-box__input" 
              type="text" 
              placeholder="Username" 
              required 
              value={username} 
              onChange={e => setUsername(e.target.value)} 
            />
          </div>
        </div>

        <div class="form__user-details-item">
          <div class="input-box">
            <input 
              class="input-box__input" 
              type="password" 
              placeholder="Password" 
              required 
              value={password} 
              onChange={e => setPassword(e.target.value)} 
            />
          </div>
        </div>

        <div class="form__user-details-item form__user-details-item--remember">
          <div class="input-box">
            <input 
              class="input-box__input input-box__input--checkbox" 
              type="checkbox" 
              checked={rememberMe} 
              onChange={e => setRememberMe(e.target.checked)} 
            />
            <label class="input-box__label input-box__label--remember">Remember me</label>
          </div>
        </div>
      </div>

      <div class="form__button-group">
        <div class="button">
          <input class="button__input" type="submit" value="Login" />
        </div>
        <div class="login">Not a member? <span>Sign up</span></div>
      </div>
    </form>
  </div>
</div>

);
};

export default Body;
