import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { upvote_none, downvote_none } from './imports'; // Assuming these are the correct imports

function Post({ user, contents }) {
  const post_id = contents._id;
  const user_id = user?._id; // Assuming user object contains _id

  const [upvote, setUpvote] = useState(0); // Initialize as 0
  const [downvote, setDownvote] = useState(0); // Initialize as 0

  useEffect(() => {
    axios.get(`http://localhost:4000/api/vote/${post_id}`)
      .then(res => {
        const { upvote, downvote } = res.data;
        setUpvote(upvote);
        setDownvote(downvote);
      })
      .catch(err => console.log(err));
  }, [post_id]);

  const vote = (like_type) => () => {
    const formData = {
      post_id,
      user_id,
      like_type
    };
    axios.post(`http://localhost:4000/api/vote`, formData)
      .then(res => {
        // Update vote count based on the response
        // You might want to fetch the new vote counts or increment/decrement based on action
        if (like_type === 1) {
          setUpvote(upvote + 1); // Assuming optimistic update
        } else if (like_type === -1) {
          setDownvote(downvote + 1); // Assuming optimistic update
        }
        console.log(res.data);
      })
      .catch(err => console.log(err));
  };

  return (
    <div className='post__container'>
      {/* Component HTML */}
      <div className='post__votes'>
        <button className='post__votes-upvote' onClick={user && vote(1)}>
          <img src={upvote_none} alt='upvote' />
        </button>
        <p>{upvote}</p>
        <button className='post__votes-downvote' onClick={user && vote(-1)}>
          <img src={downvote_none} alt='downvote' />
        </button>
        <p>{downvote}</p>
      </div>
      {/* More component HTML */}
    </div>
  );
}

export default Post;
