import React, { useState } from 'react';
import './login.css';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const history = useHistory(); // Using useHistory to redirect

  const handleSubmit = (e) => { 
    e.preventDefault();

    const formData = { username, password };
    
    axios.post("http://localhost:4000/api/login", formData, { withCredentials: true })
        .then(response => {
            console.log(response.data);
            alert("Login successful!");
            history.push("/"); // Redirect to home on successful login
        })
        .catch(error => {
            const errorMessage = error.response ? error.response.data.message : 'Login failed!';
            console.error('Login error:', errorMessage);
            alert(`Error: ${errorMessage}`);
        });
  };

  return (
    <div className="login__container">
      <div className="login__welcome">Welcome to</div>
      <div className="login__title">Taft Overflow</div>
      <form onSubmit={handleSubmit} className="login__form">
        <div className="login__user-details">
          <input
            type="text"
            placeholder="Username"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label>
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            Remember me
          </label>
        </div>
        <div className="login__actions">
          <input type="submit" value="Login" />
          Not a member? <Link to='/register'>Sign up</Link>
        </div>
      </form>
    </div>
  );
};

export default Login;
