import React, { useState } from 'react';

function CommentsSection() {
  const [comments, setComments] = useState([]);
  const [currentUserId, setCurrentUserId] = useState('Alice');
  const [newCommentText, setNewCommentText] = useState('');

  const postComment = (text, parentId = null) => {
    if (!text.trim()) {
      alert('Please enter a comment');
      return;
    }
    const newComment = {
      id: Math.random().toString(36).substr(2, 9),
      author: currentUserId,
      text: text,
      parentId,
    };
    setComments([...comments, newComment]);
    setNewCommentText('');
  };

  const renderComments = (commentsToRender, isNested = false) => {
    return commentsToRender.map(comment => (
      <div key={comment.id} className={isNested ? 'comment reply' : 'comment'}>
        <strong>{comment.author}</strong>: {comment.text}
        <button onClick={() => setNewCommentText('')}>Reply</button>
        {/* Render nested replies */}
        {comments.filter(reply => reply.parentId === comment.id).map(reply => renderComments([reply], true))}
      </div>
    ));
  };

  return (
    <div id="your-answer">
      <div className="rectangle-bar"></div>
      <select value={currentUserId} onChange={(e) => setCurrentUserId(e.target.value)}>
        <option value="Alice">Alice</option>
        <option value="Bob">Bob</option>
        <option value="Charlie">Charlie</option>
      </select>
      <div className="answer-header">
        <span>Your Answer</span>
      </div>
      <div className="answer-content">
        <textarea
          className="commentInput"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
          placeholder="Write a comment..."
        />
      </div>
      <button onClick={postComment} id='submit-button'>Submit</button>
      
      {/* The part of the component that renders comments */}
      {/* ... */}
    </div>
  );
}


export default CommentsSection;
