import React, { useState } from 'react';

// A single comment component
const Comment = ({ comment, onReply }) => {
  const [showReply, setShowReply] = useState(false);
  const [replyText, setReplyText] = useState('');

  const handleReply = () => {
    onReply(replyText, comment.id);
    setReplyText('');
    setShowReply(false);
  };

  return (
    <div className="comment">
      <div className="comment-content">
        <strong>{comment.author}:</strong> {comment.text}
      </div>
      <button onClick={() => setShowReply(true)}>Reply</button>
      {showReply && (
        <div className="comment-reply">
          <textarea value={replyText} onChange={(e) => setReplyText(e.target.value)} />
          <button onClick={handleReply}>Submit Reply</button>
        </div>
      )}
      {comment.replies?.length > 0 && (
        <div className="comment-replies">
          {comment.replies.map(reply => (
            <Comment key={reply.id} comment={reply} onReply={onReply} />
          ))}
        </div>
      )}
    </div>
  );
};

// The main comments section component
const CommentsSection = () => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const postComment = (text, parentId = null) => {
    if (!text.trim()) {
      alert('Please enter a comment or reply.');
      return;
    }
    const newComment = {
      id: Math.random().toString(36).substr(2, 9),
      author: 'Anonymous', // You might want to add functionality to change this
      text: text,
      replies: [],
    };

    if (parentId) {
      setComments(comments =>
        comments.map(comment =>
          comment.id === parentId
            ? { ...comment, replies: [...comment.replies, newComment] }
            : comment
        )
      );
    } else {
      setComments([...comments, newComment]);
    }
  };

  return (
    <div className="comments-section">
      <textarea
        value={newComment}
        onChange={(e) => setNewComment(e.target.value)}
        placeholder="Write a comment..."
      />
      <button onClick={() => postComment(newComment)}>Post Comment</button>
      <div className="comments-list">
        {comments.map(comment => (
          <Comment key={comment.id} comment={comment} onReply={postComment} />
        ))}
      </div>
    </div>
  );
};

export default CommentsSection;
