  const [downvote,setDownvote] = useState([])
  const [comments, setComments] = useState([]);

  const fetchVotes = () => {
    axios.get(`http://localhost:4000/api/vote/${post_id}`)
      .then(res => {
        const { upvote, downvote } = res.data;
        setUpvote(upvote);
        setDownvote(downvote);
      })
      .catch(err => console.log(err));
  };

